const changingElemReducer = (state, action) => {
  switch (action.type) {
    case 'CHANGE_RATING':
      return {
        ...state,
        rating: action.value 
      }
      case 'CHANGE_CATEGORY':
        return {
          ...state,
          category: action.value 
        }
    default:
      return state;
  }
};

export default changingElemReducer
