import React from 'react';
import styles from './index.module.css';
import Text from '../../Text/Text'
import Rating from './Star/Rating';
import classnames from 'classnames'

function ActivitiesElem({props, expand}) {
    const { 
        id,
        name,
        category,
        description,
        time,
        rating,
        minimized
    } = props;

    let color;
    switch (category) {
        case "active":
            color = '#8797EB';
            break;
        case "collective":
            color = '#6FCF97';
            break;
        case "outdoors":
            color = '#BB6BD9';
            break;
        default:
            color = '#C4C4C4';    
    }

    const onMaximize = (event) => {
        console.log("click");     
        expand(id);
    }
    if (minimized === true) {
        return (
            <form className = {styles.wrapper} style={{ backgroundColor: color}} onClick = {onMaximize}>
                <div className = {styles.brow}>
                    <Text textStyle = {2} color = {'#FFFFFF'}>{name}</Text>
                    <Rating rating={rating}></Rating>
                </div>
            </form>
            )
    } else {
        return (

            <form className = {styles.wrapper} style={{border: '0.5vh solid', borderColor: color}} onClick = {onMaximize}>
                <div className = {styles.brow}>
                    <Text textStyle = {2} color = {color}>{name}</Text>
                    <Rating rating={rating}></Rating>
                </div>
            </form>
            )
    }

}

export default ActivitiesElem
