import React from 'react';

function HoursInput({props}) {
    const [ time_values, goToNext] = props;

    const addHandler = (event) => {
        const text = event.target.value
        time_values.hours = text
        goToNext()
    }
    return (
        <input
            id = "hours"
            onInput = {addHandler}
            className = "time-input__input" 
            placeholder="00"
            pattern="^(2[0-3]|[01]?[0-9])" required
        />
    )
}
export default HoursInput
