import React from 'react';
import Article from './Article';
import Home from './Home'

import About from './About'
import NotFound from './NotFound'
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';


const articles = [
    {
        id:1,
        title: 'Название1',
        text: 'Текст статьи1'
    },
    {
        id:2,
        title: 'Название2',
        text: 'Текст статьи2'
    },
    {
        id:3,
        title: 'Название3',
        text: 'Текст статьи3'
    },
    {
        id:4,
        title: 'Название4',
        text: 'Текст статьи4'
    }
];

function App() {
    return (
        <div>
            <nav>
                <a href='/'>home</a>
                <a href='/about'>about</a>
            </nav>

            <Router>
                <Switch>
                    <Route exact path='/' component={Home} />
                    <Route exact path='/about' component={About}/>
                    <Route component={NotFound} />
                </Switch>
            </Router> 

            <h1>Привет </h1>
            {articles.map(article => <Article key={article.id} title = {article.title} text = {article.text}/>)}
        </div>
    )
}
export default App
