import React from 'react';
import styles from './index.module.css';

function Point({color, picked, onClick}) {
    if (picked === true) {
        return (
            <div>
                <span/>
                <span className = {styles.dot} onClick = {onClick} style={{border: '0.5vh solid', borderColor: color, backgroundColor: "white"}}/>
            </div>
        )
    } else {
        return (
            <span className = {styles.dot} onClick = {onClick} style={{border: '0.5vh solid', width: "6px", height: "6px", borderRadius: "50%", borderColor: color, display: "inline-block", backgroundColor: color}}/>
        )
    }
}

export default Point
