import React, {useState} from 'react';
import "./time-input.css"

function TimeInput() {
    const addHandler = (event) => {
        event.preventDefault();
        let value_ = event.target.value;
        if (value_.length == 2) {
            value_ += ":";
        }
        return value_;
    }
    return (
        <input 
            class = "time-input"
            placeholder ="00:00"
            onInput = {addHandler}
            pattern="^(2[0-3]|[01]?[0-9]):([0-5]?[0-9])$" required>
        </input>
    )
}

export default TimeInput;
