import {createStore} from 'redux'
import {Provider} from 'react-redux'
import activitiesListReducer from '../../reducers/activities/activitiesList'
import {signupAction} from "../../actions/user/user"
import ActivitiesList from './ActivitiesList/ActivitiesList';
import React from 'react';
import {connect} from "react-redux";

const initialState =  [{
    id: 1,
    name: "Пошутить",
    category: "outdoors",
    description: "Смешно пошутить",
    time: "12:22",
    rating: 4,
    minimized: true
  }];


function Activities() {
    const store = createStore(activitiesListReducer, initialState)
    return (
        <Provider store={store}>
            <ActivitiesList/>
        </Provider>
    )
}

const mapDispatchToProps = (dispatch) => {
    return {
      signup: (...args) => dispatch(signupAction(...args))
    }
  };

export default connect(null, mapDispatchToProps)(Activities)
