import React, {useState} from 'react';
import styles from './index.module.css';
import Star from './Star'
import {connect} from 'react-redux'
import {fetchCHANGING} from '../../../../actions/activities/changingElem'

function RatingInput(props) {
    const cnt = props.rating.rating
    console.log(cnt)

    let init_state = ["", "", "", "", ""]
    for (let i = 0; i < cnt; i++) {
        init_state[i] = "YELLOW";
    }

    const [colors, changeColors] = useState(init_state)

    const onChange = (value) => {
        props.dispatch(fetchCHANGING(value))
    }

    const onFocus = () => {
        changeColors(["GRAY", "GRAY", "GRAY", "GRAY", "GRAY"])

    }
    return (
        <div className={styles.wrapper}>
            <Star color = {colors[0]}></Star>
            <Star color = {colors[1]}></Star>
            <Star color = {colors[2]}></Star>
            <Star color = {colors[3]}></Star>
            <Star color = {colors[4]}></Star>
        </div>
    )
}

const mapStateToProps = function(store) {
    return {rating: store}
}

export default connect(mapStateToProps)(RatingInput)
