import React, { useState } from 'react';
import styles from './index.module.css';
import Input from '../Input/Input';
import Button from '../Button/Button';

function isCorrectEmail(email) {
    return email.length >= 5
}

function isCorrectName(name) {
    return name.length >= 5
}

function isCorrectPassword(password) {
    return password.length >= 5
}

function Signup() {
    const [user_data, setUserData] = useState({
        email: '',
        password: '',
        name: '',
        emailError: false,
        passwordError: false,
        nameError: false,
        errorText: ''
    });

    const onSignUp = (event) => {
        event.preventDefault();
        if (!isCorrectName(user_data.name)) {
            setUserData({
                email: user_data.email,
                password: user_data.password,
                name: user_data.name,
                emailError: user_data.emailError,
                passwordError: user_data.passwordError,
                nameError: true,
                errorText: 'Name must be longer than 5 characters'
            });
            return
        }

        if (!isCorrectEmail(user_data.email)) {
            setUserData({
                email: user_data.email,
                password: user_data.password,
                name: user_data.name,
                emailError: true,
                passwordError: user_data.passwordError,
                nameError: user_data.nameError,
                errorText: 'Email must be longer than 5 characters'
            });

            return
        }

        if (!isCorrectPassword(user_data.password)) {
            setUserData({
                email: user_data.email,
                password: user_data.password,
                name: user_data.name,
                emailError: user_data.emailError,
                passwordError: true,
                nameError: user_data.nameError,
                errorText: 'Password must be longer than 5 characters'
            });

            return
        }
    };

    const onChangeEmail = (event) => {
        setUserData({
            email: event.target.value,
            password: user_data.password,
            name: user_data.name,
            emailError: false,
            passwordError: false,
            nameError: false,
            errorText: false
        });
    };

    const onChangePassword = (event) => {
        setUserData({
            email: user_data.email,
            password: event.target.value,
            name: user_data.name,
            emailError: false,
            passwordError: false,
            nameError: false,
            errorText: false
        });
    };

    const onChangeName = (event) => {
        setUserData({
            email: user_data.email,
            password: user_data.password,
            name: event.target.value,
            emailError: false,
            passwordError: false,
            nameError: false,
            errorText: false
        });
    };


    const onToLogin = (event) => {
        event.preventDefault();

        // this.props.history.push('/login');
    }
    return ( <
        form className = { styles.wrapper } >
        <
        h2 > Добро пожаловать < /h2> <
        div className = { styles.inputs } >
        <
        Input placeholder = "Name"
        onChange = { onChangeName }
        value = { user_data.name }
        error = { user_data.nameError }
        /> <
        Input type = "email"
        placeholder = "Email"
        onChange = { onChangeEmail }
        value = { user_data.email }
        error = { user_data.emailError }
        /> <
        Input type = "password"
        placeholder = "Password"
        onChange = { onChangePassword }
        value = { user_data.password }
        error = { user_data.passwordError }
        /> <
        /div> <
        div className = { styles.buttons } >
        <
        Button onClick = { onSignUp }
        buttonStyle = { 1 }
        inversed = { true } > Sign Up < /Button> <
        /div> <
        p className = { styles.error } > { user_data.errorText } < /p> <
        /form>
    )
}

export default Signup;
