import userService from '../../userService/index'

function fetchStart() {
  return {
    type: 'USER_FETCHING'
  }
}

function fetchFail(payload) {
  return {
    type: 'USER_FAIL',
    payload
  }
}

function fetchSuccess(payload) {
  return {
    type: 'USER_SUCCESS',
    payload
  }
}

export function currentUserAction() {
  return dispatch => {
    dispatch(fetchStart());

    return userService.currentUser().then((data) => {
      dispatch(fetchSuccess(data))
    })
      .catch((error) => {
        dispatch(fetchFail(error))
        throw Error(error)
      })
  }
}

export function loginAction(email, password) {
  return dispatch => {
    dispatch(fetchStart());

    return userService.login(email, password).then((data) => {
      dispatch(fetchSuccess(data))

      console.log('0')
    })
      .catch((error) => {
        dispatch(fetchFail(error))
        throw Error(error)
      })
  }
}

export function signupAction(user, password) {
  return dispatch => {
    dispatch(fetchStart());

    return userService.signup(user, password).then((data) => {
      console.log("successful signup", data)
      dispatch(fetchSuccess(data))
    })
      .catch((error) => {
        console.log("signup err", error)
        dispatch(fetchFail(error))
        throw Error(error)
      })
  }
}

export function logoutAction() {
  return dispatch => {
    dispatch(fetchStart());

    return userService.logout().then((data) => {
      dispatch(fetchSuccess(null))
    })
      .catch((error) => {
        dispatch(fetchFail(error))
        throw Error(error)
      })
  }
}
