];*/

function App() {
    // eslint-disable-next-line
    const [user_data, setUserData] = useState({
        email: '',
        password: '',
        repeatPassword: '',
        emailError: false,
        passwordError: false,
        repeatPasswordError: false,
        errorText: ''
      });
    return (
        <Context.Provider value = {{setUserData}}>
            <div className = 'app'>

                <Router>
                    <Switch>
                        <Route exact path='/' component={Main} />
                        <Route exact path='/about' component={About}/>
                        <Route component={NotFound} />
                    </Switch>
                </Router> 
            </div>
        </Context.Provider>
    )
}

// {articles.map(article => <Article key={article.id} title = {article.title} text = {article.text}/>)}
export default App
