import React, {useContext} from 'react';
import styles from './index.module.css';
import Input from '../Input/Input';
import Button from '../Button/Button';
import {Context} from "../../context";

function isCorrectEmail(email) {
  return email.length >= 5
}

function isCorrectPassword(email) {
    console.log("email: ", email)
  return email.length >= 5
}

function Signup() {
    const {user_data, setUserData} = useContext(Context);
    const resetAllErrors = (event) => {
        let prev_user_data = user_data;
        prev_user_data.emailError = false;
        prev_user_data.passwordError = false;
        prev_user_data.repeatPasswordError = false;
        prev_user_data.errorText = '';
        setUserData(prev_user_data);
    };

    const onSignUp = (event) => {
        event.preventDefault();

        let prev_user_data = user_data;

        if (!isCorrectEmail(prev_user_data.email)) {
            prev_user_data.emailError = true;
            prev_user_data.errorText = 'Логин должен быть длиннее 5 символов';
            setUserData(prev_user_data);

            return
        }

        if (!isCorrectPassword(prev_user_data.password)) {
            prev_user_data.passwordError = true;
            prev_user_data.errorText = 'Пароль должен быть длиннее 5 символов';
            setUserData(prev_user_data);

          return
        }

        if (prev_user_data.password !== prev_user_data.repeatPassword) {
            prev_user_data.passwordError = true;
            prev_user_data.repeatPasswordError = true;
            prev_user_data.errorText = 'Пароли не совпадают';
            setUserData(prev_user_data);
          return
        }


        /*UserService.signup(email, password)
          .then((user) => {
            this.props.setUser(user);


            this.props.history.push('/');
          })
          .catch((error) => {
            this.setState({errorText: error.error})
          })*/

    };

    const onChangeEmail = (event) => {
        resetAllErrors();
        let prev_user_data = user_data;
        prev_user_data.email = event.target.value;
        setUserData(prev_user_data)
    };

    const onChangePassword = (event) => {
        resetAllErrors();
        let prev_user_data = user_data;
        prev_user_data.password = event.target.value;
        setUserData(prev_user_data)
    };

    const onChangeRepeatPassword = (event) => {
        resetAllErrors();
        let prev_user_data = user_data;
        prev_user_data.repeatPassword = event.target.value;
        setUserData(prev_user_data)
    };


    const onToLogin = (event) => {
        event.preventDefault();

        // this.props.history.push('/login');
    }
    return (
        <form className={styles.wrapper}>
            <h2>Добро пожаловать</h2>
            <div className={styles.inputs}>
            <Input name="email" type="email" placeholder="Логин" onChange={onChangeEmail} value={user_data.email} error={user_data.emailError}/>
            <Input name="password" type="password" placeholder="Пароль" onChange={onChangePassword} value={user_data.password} error={user_data.passwordError}/>
            <Input name="repeatPassword" type="password" placeholder="Повторите пароль" onChange={onChangeRepeatPassword} value={user_data.repeatPassword} error={user_data.repeatPasswordError}/>
            </div>
            <div className={styles.buttons}>
            <Button primary onClick={onSignUp}>Регистрация</Button>
            <Button onClick={onToLogin}>Уже зареган</Button>
            </div>
            <p className={styles.error}>{user_data.errorText}</p>
        </form>
    )
}

export default Signup;
