import {Provider} from 'react-redux'
import {connect} from "react-redux";
import ActivitiesElemPresentation from '../ActivitiesFrame/ActivitiesElem/ActivitiesElemPresentation'  
import styles from './index.module.css';
import React, {useState, useEffect} from 'react';
import Text from '../Text/Text'
import Button from '../Button/Button'

function getMinutes(time) {
    return parseInt(time.substring(0, 2)) * 60 + parseInt(time.substring(3, 5));
}

function getResponse(data, request) {
    let max = getMax(data, getMinutes(request));
    return max.data;
}

function getMax(data, minutes) {
    if (data.length === 0) {
        return {
            data: [],
            sum: 0
        };
    }
    let max_sum = 0;
    let max_data = [];
    data.forEach((item, index, array)=> {
        let curr_data = data.slice();
        let removed = curr_data.splice(index, 1);
        let curr_minutes = getMinutes(removed[0].time);
        if (curr_minutes <= minutes) {
             let curr = getMax(curr_data, minutes - getMinutes(removed[0].time))
            if (curr.sum + removed[0].rating * curr_minutes > max_sum) {
                max_sum = curr.sum + removed[0].rating * curr_minutes; 
                max_data = removed.concat(curr.data)
            }
        }
    });
    return {
        data: max_data,
        sum: max_sum
    };
}



function Response(props) {
    const [move, setMove] = useState(false)
    useEffect(() => {
        setMove(true)
      }, []);

    const request = props.request.request
    // WARNING!!! VERY BAD REALISATION
    const response = getResponse(props.data, request)
    let onToActivities =(event) => {
        event.preventDefault();

        props.history.push('/activities');
    }
    let onToMain =(event) => {
        event.preventDefault();

        props.history.push('/');
    }
    return (
        <div className = {styles.mainFrame}>
            <Text textStyle={0} color_name={'blue'}>RELAX</Text>
            <div>
                <Text>{request}</Text>
                <div className = {styles.activities_list}>
                    {response.map((elem, index, array) =>
                        <ActivitiesElemPresentation key = {elem.id} props={elem} moving = {move} delay = {100 * (array.length - index)}> 
                        </ActivitiesElemPresentation>
                    )} 
                </div>
                <svg width="316" height="155" viewBox="0 0 316 155" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M22.2088 23L7.89566e-06 64.6842L0 155L316 155L316 64.6842L295.06 23L22.2088 23Z" fill="#828282"/>
                    <path d="M150 58C101.951 58 63 97.3989 63 146H237C237 97.3989 198.049 58 150 58Z" fill="#F2F2F2"/>
                    <rect x="145" y="62" width="6" height="18" fill="#333333"/>
                    <rect x="90.1044" y="89.515" width="6.34538" height="17.7671" transform="rotate(-45 90.1044 89.515)" fill="#333333"/>
                    <rect x="69" y="142" width="6" height="17" transform="rotate(-90 69 142)" fill="#333333"/>
                    <rect x="206.732" y="85.0281" width="6.34538" height="17.7671" transform="rotate(45 206.732 85.0281)" fill="#333333"/>
                    <rect x="232" y="136" width="6" height="18" transform="rotate(90 232 136)" fill="#333333"/>
                    <path d="M49.5 123L52.3064 131.637H61.3882L54.0409 136.975L56.8473 145.613L49.5 140.275L42.1527 145.613L44.9591 136.975L37.6118 131.637H46.6936L49.5 123Z" fill="#FBFF4E"/>
                    <path d="M77 60L79.6942 68.2918H88.4127L81.3593 73.4164L84.0534 81.7082L77 76.5836L69.9466 81.7082L72.6407 73.4164L65.5873 68.2918H74.3058L77 60Z" fill="#FBFF4E"/>
                    <path d="M148.5 30L151.306 38.6373H160.388L153.041 43.9754L155.847 52.6127L148.5 47.2746L141.153 52.6127L143.959 43.9754L136.612 38.6373H145.694L148.5 30Z" fill="#FBFF4E"/>
                    <path d="M228 60L230.694 68.2918H239.413L232.359 73.4164L235.053 81.7082L228 76.5836L220.947 81.7082L223.641 73.4164L216.587 68.2918H225.306L228 60Z" fill="#BABABA"/>
                    <path d="M252.5 123L255.306 131.637H264.388L257.041 136.975L259.847 145.613L252.5 140.275L245.153 145.613L247.959 136.975L240.612 131.637H249.694L252.5 123Z" fill="#BABABA"/>
                    <rect x="6" width="304" height="19" fill="#828282"/>
                </svg>
            </div>
            <div className = {styles.column}>
                <Button onClick = {onToMain} inversed={true}>
                        Override relax time
                </Button> 
                <Button onClick = {onToActivities} inversed={false}>
                        Go to my activities
                </Button> 
            </div>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
      data: state.data    
    }
  };

export default connect(mapStateToProps)(Response)
