import React, {useState} from 'react';
import styles from './index.module.css';
import Star from './Star'


function RatingInput({rating, changeRating}) {
    const cnt = rating.rating

    let init_state = ["", "", "", "", ""]
    for (let i = 0; i < cnt; i++) {
        init_state[i] = "YELLOW";
    }

    const [colors, changeColors] = useState(init_state)

    const onClick = () => {
        changeRating(5)
    }

    const onFocus = () => {
        changeColors(["GRAY", "GRAY", "GRAY", "GRAY", "GRAY"])

    }
    return (
        <div className={styles.wrapper} onClick = {onClick}>
            <Star color = {colors[0]}></Star>
            <Star color = {colors[1]}></Star>
            <Star color = {colors[2]}></Star>
            <Star color = {colors[3]}></Star>
            <Star color = {colors[4]}></Star>
        </div>
    )
}

export default RatingInput
