import React from 'react';
import styles from './index.module.css';
import classnames from 'classnames'
import Title from '../../Title/Title'

function ActivitiesElem({props, expand}) {
    const { 
        id,
        name,
        category,
        description,
        time,
        rating,
        minimized
    } = props;

    const onMaximize = (event) => {
        console.log("click");
        expand(id);
    }

    const wrapper_class = classnames(styles.wrapper, {
        [styles.inversed]: !!minimized
      });

    if (minimized === true) {
        return (
            <form className = {styles.wrapper, styles.inversed} onClick = {onMaximize}>
                <div className = {styles.brow}>
                    <Title titleStyle = {2} inversed = {true}>{name}</Title>
                </div>
            </form>
            )
    } else {
        return (
            <div onClick = {onMaximize}>
                <h2>{name}</h2>
                <p>
                    Category: {category}
                    Description: {description}
                </p>
            </div>
            )
    }

}

export default ActivitiesElem
