import React, {useState} from 'react';
import "./time-input.css"
import HoursInput from './HoursInput'
import MinutesInput from './MinutesInput'

function TimeInput() {
    const [time_values, setTimeValues] = useState({hours: "0", minutes: "0"});
    const goToNext = (hours, minutes) => {
        let hours_ = hours;
        let minutes_ = minutes;
        console.log(hours_, minutes_);
        /*if (hours_.length > 2) {
            minutes_ = hours_[2];
            hours_ = hours_.substring(0, 2);
        }
        setTimeValues(hours_, minutes_); */
    }
    const goToPrev = (hours, minutes) => {
        let hours_ = hours;
        let minutes_ = minutes;
        console.log(hours_, minutes_);
        /*if (minutes_.length === 0) {
            hours_ = hours_.substring(0, 1);
        }
        setTimeValues(hours_, minutes_); */
    }
    return (
        <div class = "time-input">
            <HoursInput time_values = {time_values} goToNext = {goToNext} setTimeValues = {setTimeValues}/>
            <label class = "colon">:</label>    
            <MinutesInput  time_values = {time_values} goToPrev = {goToPrev} setTimeValues = {setTimeValues}/>
        </div>
    )
}

export default TimeInput;
