import React from "react";
import classnames from 'classnames'
import styles from "./index.module.css";

function Text({children, titleStyle, color}) {
    let title_style;
    switch(titleStyle) {
        case 0: 
            title_style = styles.main;
            break;
        case 1:
            title_style = styles.secondary;
            break;
        case 2:
            title_style = styles.activity_name;
            break;    
        default:
            title_style = styles.secondary;    
    }
    return (
        <h1 className={title_style} style={{color: color}}>{children}</h1>
    )
}


export default Text
