import React from 'react';

function HoursInput({time_values, goToNext, setTimeValues}) {    
    const addHandler = (event) => {
        setTimeValues({hours: event.target.value, minutes: time_values.minutes});
        goToNext(time_values.hours, time_values.minutes)
    }
    return (
        <input
            id = "hours"
            onInput = {addHandler}
            className = "time-input__input" 
            placeholder="00"
            pattern="^(2[0-3]|[01]?[0-9])" required
        />
    )
}
export default HoursInput
