import React from 'react';
import styles from './index.module.css';
import Input from '../../Input/Input'
import RatingInput from '../ActivitiesElem/Star/RatingInput';
import changingElemReducer from '../../../reducers/activities/changingElem'
import {createStore} from 'redux'
import {Provider} from 'react-redux'
import Text from '../../Text/Text'

function ChangingElem() {
    const store = createStore(changingElemReducer, {rating: 1, category: "active"})

    let color;
    switch (store.category) {
        case "active":
            color = '#8797EB';
            break;
        case "collective":
            color = '#6FCF97';
            break;
        case "outdoors":
            color = '#BB6BD9';
            break;
        default:
            color = '#BB6BD9';    
    }
    return (
        <Provider store={store}>
            <div className={styles.container}>
                <div className = {styles.frame} style={{border: '0.5vh solid', borderColor: color}}>
                    <div className = {styles.brow}>
                        <Input placeholder="Enter name" inputStyle = {1}/>
                        <RatingInput></RatingInput>
                    </div>
                    <Text textStyle = {3} color_name = {"gray"} onClick = {() => {
                        console.log("click")
                        store.category = "outdoors"
                    }}>Choose category:</Text>
                </div>
            </div>
        </Provider>
    )
}

export default ChangingElem
