import React from 'react';
import "./time-input.css"

function goToNext(event) {
    const hours = document.getElementsByClassName('time-input__input')[0];
    const minutes = document.getElementsByClassName('time-input__input')[1];
    console.log(hours.textContent);
    console.log(minutes.textContent);
}

function goToPrev(event) {
    console.log(event.target.value);
}

function TimeInput() {
    return (
        <div class = "time-input">
            <input
                onInput = {goToNext}
                className = "time-input__input" 
                placeholder="00"
                 pattern="^(2[0-3]|[01]?[0-9])" required
            />
            <label class = "colon">:</label>    
            <input 
                onInput = {goToPrev}
                className = "time-input__input"
                placeholder="00"
                pattern="([0-5]?[0-9])$" required
            />
        </div>
    )
}
export default TimeInput
