let current_data = {
    user: {login: "", email: ""},
    password: ""
}

let data = [
    {user: {login: "trrttrtrsrfewrwed", email: "nurgaliev.tr@phystech.edu"},
     password: "qwerty"}
]

function resolveAfter2Seconds(x) {
    return new Promise(resolve => {
      setTimeout(() => {
        resolve(x);
      }, 2000);
    });
}

function rejectAfter1Second(error) {
    return new Promise((resolve, reject) => {
        setTimeout(() => {
          reject(error);
        }, 1000);
      });
}

async function addUser(user, password) {
    if (data.find((elem) => { return elem.user.email == user.email}) == null) {
        console.log("addUser OK")
        data.push({user: user, password: password})
        current_data = {user: user, password: password}
        return resolveAfter2Seconds(current_data)
    } else {
        console.log("addUser REJECT")
        return rejectAfter1Second("user with such email already exists")
    }   
} 

async function enter(email, password) {
    console.log("enter", email, password)
    let find_result = data.find((elem) => { return elem.user.email == email && elem.password == password})
    console.log(find_result)
    if (find_result == null) {
        console.log("enter REJECT")
        return rejectAfter1Second("incorrect email or password")
    } else {
        console.log("enter OK")
        current_data = find_result
        return resolveAfter2Seconds(current_data)
    }   
} 

async function lout() {
    console.log("logout")
    current_data = {
        user: {login: "", email: ""},
        password: ""
    }
    return resolveAfter2Seconds(null);
}

async function cUser() {
    if (current_data.user === "") {
        console.log("currentUser REJECT")
        return rejectAfter1Second("you need to log in first")
    } else {
        console.log("currentUser OK")
        return resolveAfter2Seconds(current_data);
    }
}

const UserService = {
  async signup(user, password) {
    return addUser(user, password)
    //return http('signup', 'POST', {user, password})
  },
  async login(email, password) {
    console.log('login');
    return enter(email, password)
    //return http('signin', 'POST', {email, password})
  },
  async logout() {
      return lout()
    //return http('signout', 'DELETE')
  },
  async currentUser() {
      return cUser()
    //return http('currentUser', 'GET')
  }
};

export default UserService;
