import {Provider} from 'react-redux'
import {connect} from "react-redux";
import ActivitiesElemPresentation from '../ActivitiesFrame/ActivitiesElem/ActivitiesElemPresentation'  
import styles from './index.module.css';
import React, {useState, useEffect} from 'react';
import Text from '../Text/Text'
import Button from '../Button/Button'

function getMinutes(time) {
    return parseInt(time.substring(0, 2)) * 60 + parseInt(time.substring(3, 5));
}

function getResponse(data, request) {
    let max = getMax(data, getMinutes(request));
    return max.data;
}

function getMax(data, minutes) {
    if (data.length === 0) {
        return {
            data: [],
            sum: 0
        };
    }
    let max_sum = 0;
    let max_data = [];
    data.forEach((item, index, array)=> {
        let curr_data = data.slice();
        let removed = curr_data.splice(index, 1);
        let curr_minutes = getMinutes(removed[0].time);
        if (curr_minutes <= minutes) {
             let curr = getMax(curr_data, minutes - getMinutes(removed[0].time))
            if (curr.sum + removed[0].rating * curr_minutes > max_sum) {
                max_sum = curr.sum + removed[0].rating * curr_minutes; 
                max_data = removed.concat(curr.data)
            }
        }
    });
    return {
        data: max_data,
        sum: max_sum
    };
}



function Response(props) {
    const [move, setMove] = useState(false)
    useEffect(() => {
        setMove(true)
      }, []);

    const request = props.request.request
    // WARNING!!! VERY BAD REALISATION
    const response = getResponse(props.data, request)
    let onToActivities =(event) => {
        event.preventDefault();

        props.history.push('/activities');
    }
    let onToMain =(event) => {
        event.preventDefault();

        props.history.push('/');
    }
    return (
        <div className = {styles.mainFrame}>
            <Text textStyle={0} color_name={'blue'}>RELAX</Text>
            <div>
                <Text>{request}</Text>
                <div className = {styles.activities_list}>
                    {response.map((elem, index, array) =>
                        <ActivitiesElemPresentation key = {elem.id} props={elem} moving = {move} delay = {100 * (array.length - index)}> 
                        </ActivitiesElemPresentation>
                    )} 
                </div>
                <svg width="249" height="123" viewBox="0 0 249 123" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M17.5 18L-2.42668e-05 51L-3.05176e-05 122.5L249 122.5V51L232.5 18L17.5 18Z" fill="#828282"/>
                    <rect x="114" y="49" width="5" height="14" fill="#333333"/>
                    <rect x="71" y="70.5355" width="5" height="14" transform="rotate(-45 71 70.5355)" fill="#333333"/>
                    <rect x="54" y="112" width="5" height="14" transform="rotate(-90 54 112)" fill="#333333"/>
                    <rect x="162.9" y="67" width="5" height="14" transform="rotate(45 162.9 67)" fill="#333333"/>
                    <rect x="183" y="107" width="5" height="14" transform="rotate(90 183 107)" fill="#333333"/>
                    <path d="M38.75 97L40.939 103.737H48.0228L42.2919 107.901L44.4809 114.638L38.75 110.474L33.0191 114.638L35.2081 107.901L29.4772 103.737H36.561L38.75 97Z" fill="#FBFF4E"/>
                    <path d="M60.75 47L62.939 53.7371H70.0228L64.2919 57.9008L66.4809 64.6379L60.75 60.4742L55.0191 64.6379L57.2081 57.9008L51.4772 53.7371H58.561L60.75 47Z" fill="#FBFF4E"/>
                    <path d="M116.75 24L118.939 30.7371H126.023L120.292 34.9008L122.481 41.6379L116.75 37.4742L111.019 41.6379L113.208 34.9008L107.477 30.7371H114.561L116.75 24Z" fill="#FBFF4E"/>
                    <path d="M179.75 47L181.939 53.7371H189.023L183.292 57.9008L185.481 64.6379L179.75 60.4742L174.019 64.6379L176.208 57.9008L170.477 53.7371H177.561L179.75 47Z" fill="#BABABA"/>
                    <path d="M198.75 97L200.939 103.737H208.023L202.292 107.901L204.481 114.638L198.75 110.474L193.019 114.638L195.208 107.901L189.477 103.737H196.561L198.75 97Z" fill="#BABABA"/>
                    <rect x="5" width="239" height="15" fill="#828282"/>
                </svg>
            </div>
            <div className = {styles.column}>
                <Button onClick = {onToMain} inversed={true}>
                        Override relax time
                </Button> 
                <Button onClick = {onToActivities} inversed={false}>
                        Go to my activities
                </Button> 
            </div>
        </div>
    )
}

const mapStateToProps = (state) => {
    return {
      data: state.data    
    }
  };

export default connect(mapStateToProps)(Response)
