import React, {useState} from 'react';
import styles from "./index.module.css"

function TimeInput() {
    const [prev_length, setPrevLength] = useState(-1);
    const addHandler = (prev_length, setPrevLength, event) => {
        event.preventDefault();
        let value_ = event.target.value;
        if (!Number.isNan(value_[value_.length - 1])) {
            if (value_.length === 2) {
                if (prev_length < 2) {
                    value_ += ":";
                } else {
                    value_ = value_[0];
                }
            }
            if (value_.length > 4) {
                value_ = value_.substring(0, 5);
            }
            setPrevLength(value_.length);
            event.target.value = value_;
        }
    }
    return (
        <input 
            placeholder ="00:00"
            onInput = {(event) => addHandler(prev_length, setPrevLength, event)}
            pattern="^(2[0-3]|[01]?[0-9]):([0-5]?[0-9])$" required>
        </input>
    )
}

export default TimeInput;
