import React from 'react';

function ActivitiesElem({props, expand}) {
    const { 
        id,
        name,
        category,
        description,
        time,
        rating,
        minimized
    } = props;

    const onMaximize = (event) => {
        console.log("click");
        expand(id);
    }

    if (minimized === true) {
        return (
            <div onClick = {onMaximize}>
                <h2>{name}</h2>
                <p>
                    Category: {category}
                </p>
            </div>
            )
    } else {
        return (
            <div onClick = {onMaximize}>
                <h2>{name}</h2>
                <p>
                    Category: {category}
                    Description: {description}
                </p>
            </div>
            )
    }

}

export default ActivitiesElem
