import React from 'react';

function MinutesInput({time_values, goToPrev, setTimeValues}) {
    const addHandler = (event) => {
        setTimeValues({hours: time_values.hours, minutes: event.target.value});
        goToPrev(time_values.hours, time_values.minutes)
    }
    return (
        <input 
            id = "minutes"
            onInput = {addHandler}
            className = "time-input__input"
            placeholder="00"
            pattern="([0-5]?[0-9])$" required
        />
    )
}
export default MinutesInput
