import React, {useState} from 'react';
import "./time-input.css"
import HoursInput from './HoursInput'
import MinutesInput from './MinutesInput'

function TimeInput() {
    const [time_values, setTimeValues] = useState({hours: "0", minutes: "0"});
    goToNext = () => {
        const [hours, minutes] = time_values;
        if (hours.length > 2) {
            minutes = hours[2];
            hours = hours.substring(0, 2);
        }
        setTimeValues(hours, minutes);
    }
    return (
        <div class = "time-input">
            <HoursInput time_values = {time_values} goToNext = {goToNext}/>
            <label class = "colon">:</label>    
            <MinutesInput  time_values = {time_values} goToPrev = {goToPrev}/>
        </div>
    )
}

export default TimeInput;
