import React from 'react';

function MinutesInput({time_values, setTimeValues}) {
    const addHandler = (event) => {
        let hours_ = time_values.hours;
        let minutes_ = event.target.value;
        console.log(hours_, minutes_);
        if (minutes_.length === 0) {
            hours_ = hours_.substring(0, 1);
        }
        setTimeValues({hours: hours_, minutes: minutes_});
    }
    return (
        <input 
            id = "minutes"
            onInput = {addHandler}
            className = "time-input__input"
            placeholder="00"
            pattern="([0-5]?[0-9])$" required
        />
    )
}
export default MinutesInput
