import React, {useState} from 'react';
import styles from './index.module.css';
import Star from './Star'
import {connect} from 'react-redux'
import {fetchCHANGING} from '../../../../actions/activities/changingElem'

function RatingInput(props) {
    const cnt = props.rating.rating
    console.log(cnt)

    let colors = ["LIGHTGRAY", "LIGHTGRAY", "LIGHTGRAY", "LIGHTGRAY", "LIGHTGRAY"]
    for (let i = 0; i < cnt; i++) {
        colors[i] = "YELLOW";
    }

    for (let i = 0; i < 5; i++) {
        console.log(i, colors[i])
    }

    const onChange = (value) => {
        console.log("click")
        props.dispatch(fetchCHANGING(value))
    }

    const onFocus = () => {
        //changeColors(["GRAY", "GRAY", "GRAY", "GRAY", "GRAY"])

    }
    return (
        <div className={styles.wrapper} onClick = {(event) => {event.continuePropagation()}}>
            <Star color = {colors[0]} onClick = {() => onChange(2)}></Star>
            <Star color = {colors[1]} onClick = {() => onChange(2)}></Star>
            <Star color = {colors[2]} onClick = {() => onChange(3)}></Star>
            <Star color = {colors[3]} onClick = {() => onChange(4)}></Star>
            <Star color = {colors[4]} onClick = {() => onChange(5)}></Star>
        </div>
    )
}

const mapStateToProps = function(store) {
    return {rating: store}
}

export default connect(mapStateToProps)(RatingInput)
