import React from 'react';
import styles from './index.module.css';
import Title from '../../Title/Title'
import Rating from './Star/Rating';
import classnames from 'classnames'

function ActivitiesElem({props, expand}) {
    const { 
        id,
        name,
        category,
        description,
        time,
        rating,
        minimized
    } = props;

    let color = styles.gray;
    switch (category) {
        case "active":
            color = styles.blue;
            break;
        case "collective":
            color = styles.green;
            break;
        case "outdoors":
            color = styles.purple;
            break;
        default:
            color = styles.gray;    
    }

    const onMaximize = (event) => {
        console.log("click");
        console.log(category); 
        console.log(color);      
        expand(id);
    }

    if (minimized === true) {
        return (
            <form className = {classnames(styles.wrapper, color)} onClick = {onMaximize}>
                <div className = {styles.brow}>
                    <Title titleStyle = {2} inversed = {true}>{name}</Title>
                    <Rating rating={rating}></Rating>
                </div>
            </form>
            )
    } else {
        return (
            <div onClick = {onMaximize}>
                <h2>{name}</h2>
                <p>
                    Category: {category}
                    Description: {description}
                </p>
            </div>
            )
    }

}

export default ActivitiesElem
