import React from 'react';

function HoursInput({time_values, setTimeValues}) {    
    const addHandler = (event) => {
        let hours_ = event.target.value;
        let minutes_ = time_values.minutes;
        console.log(hours_, minutes_);
        if (hours_.length > 2) {
            minutes_ = hours_[2];
            hours_ = hours_.substring(0, 2);
        }
        setTimeValues({hours: hours_, minutes: minutes_})
    }
    return (
        <input
            id = "hours"
            onInput = {addHandler}
            className = "time-input__input" 
            placeholder="00"
            pattern="^(2[0-3]|[01]?[0-9])" required
        />
    )
}
export default HoursInput
