const initialState =  [{
    id: 1,
    name: "Пошутить kjkjkjkjkjkjkjkjkjkj",
    category: "shit",
    description: "Смешно пошутить isisiisisisiissiisisi tyjhtyjtjtyjtyj",
    time: " >1h",
    rating: 5,
    minimized: true
  }];

  const activitiesListReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'MAXIMIZE':
        return state.map(elem=>{
          if (elem.id === action.id) {
            elem.minimized = false;
          } else {
            elem.minimized = true;
          }
          return elem;
        })
      case 'MINIMIZE':
        return state.map(elem=>{
          if (elem.id === action.id) {
            elem.minimized = true;
          } 
          return elem;
        })
      default:
        return state;
    }
  };

  export default activitiesListReducer
