import React, {useState} from 'react';
import "./time-input.css"

function TimeInput() {
    const [prev_length, setPrevLength] = useState(-1);
    const addHandler = (prev_length, setPrevLength, event) => {
        event.preventDefault();
        let value_ = event.target.value;
        if (value_.length == 2) {
            if (prev_length < 2) {
                value_ += ":";
            } else {
                value_ = value_[0];
            }
        }
        event.target.value = value_ + " ";
    }
    return (
        <input 
            class = "time-input"
            placeholder ="00:00"
            onInput = {(event) => addHandler(prev_length, setPrevLength, event)}
            pattern="^(2[0-3]|[01]?[0-9]):([0-5]?[0-9])$" required>
        </input>
    )
}

export default TimeInput;
