import React from 'react';

function ActivitiesElem({props, expand}) {
    const { 
        id,
        name,
        category,
        description,
        time,
        rating,
        minimized
    } = props;

    if (minimized === true) {
        return (
            <div onClick = {(event) => expand(id)}>
                <h2>{name}</h2>
                <p>
                    Category: {category}
                </p>
            </div>
            )
    } else {
        return (
            <div onClick = {(event) => {
                console.log("click");
                expand(id);}}>
                <h2>{name}</h2>
                <p>
                    Category: {category}
                    Description: {description}
                </p>
            </div>
            )
    }

}

export default ActivitiesElem
