import React, {useContext} from 'react';
import styles from './index.module.css';
import Input from '../Input/Input';
import Button from '../Button/Button';
import {Context} from "../../context";

function isCorrectEmail(email) {
  return email.length >= 5
}

function isCorrectPassword(email) {
    console.log("email: ", email)
  return email.length >= 5
}

function Signup() {
    const {user_data, setUserData} = useContext(Context);

    const resetAllErrors = (event) => {
        setUserData({
            email: user_data.email,
            password: user_data.password,
            repeatPassword: user_data.repeatPassword,
            emailError: false,
            passwordError: false,
            repeatPasswordError: false,
            errorText: ''
          });
    };

    const onSignUp = (event) => {
        event.preventDefault();

        if (!isCorrectEmail(user_data.email)) {
            setUserData({
                email: user_data.email,
                password: user_data.password,
                repeatPassword: user_data.repeatPassword,
                emailError: true,
                passwordError: user_data.passwordError,
                repeatPasswordError: user_data.repeatPasswordError,
                errorText: 'Логин должен быть длиннее 5 символов'
              });

            return
        }

        if (!isCorrectPassword(user_data.password)) {
            setUserData({
                email: user_data.email,
                password: user_data.password,
                repeatPassword: user_data.repeatPassword,
                emailError: user_data.emailError,
                passwordError: true,
                repeatPasswordError: user_data.repeatPasswordError,
                errorText: 'Пароль должен быть длиннее 5 символов'
              });

          return
        }

        if (user_data.password !== user_data.repeatPassword) {
            setUserData({
                email: user_data.email,
                password: user_data.password,
                repeatPassword: user_data.repeatPassword,
                emailError: user_data.emailError,
                passwordError: true,
                repeatPasswordError: true,
                errorText: 'Пароли не совпадают'
              });
          return
        }
    };

    const onChangeEmail = (event) => {
        console.log("onCangeEmail");
        resetAllErrors();
        user_data.email = event.target.value;
        setUserData({
            email: event.target.value,
            password: user_data.password,
            repeatPassword: user_data.repeatPassword,
            emailError: user_data.emailError,
            passwordError: user_data.passwordError,
            repeatPasswordError: user_data.repeatPasswordError,
            errorText: user_data.errorText
          });
        console.log(user_data);
    };

    const onChangePassword = (event) => {
        resetAllErrors();
        setUserData({
            email: user_data.email,
            password: event.target.value,
            repeatPassword: user_data.repeatPassword,
            emailError: user_data.emailError,
            passwordError: user_data.passwordError,
            repeatPasswordError: user_data.repeatPasswordError,
            errorText: user_data.errorText
          });
        console.log(user_data);
    };

    const onChangeRepeatPassword = (event) => {
        resetAllErrors();
        setUserData({
            email: user_data.email,
            password: user_data.password,
            repeatPassword: event.target.value,
            emailError: user_data.emailError,
            passwordError: user_data.passwordError,
            repeatPasswordError: user_data.repeatPasswordError,
            errorText: user_data.errorText
          });
        console.log(user_data);
    };


    const onToLogin = (event) => {
        event.preventDefault();

        // this.props.history.push('/login');
    }
    return (
        <form className={styles.wrapper}>
            <h2>Добро пожаловать</h2>
            <div className={styles.inputs}>
            <Input name="email" type="email" placeholder="Логин" onChange={onChangeEmail} value={user_data.email} error={user_data.emailError}/>
            <Input name="password" type="password" placeholder="Пароль" onChange={onChangePassword} value={user_data.password} error={user_data.passwordError}/>
            <Input name="repeatPassword" type="password" placeholder="Повторите пароль" onChange={onChangeRepeatPassword} value={user_data.repeatPassword} error={user_data.repeatPasswordError}/>
            </div>
            <div className={styles.buttons}>
            <Button primary onClick={onSignUp}>Регистрация</Button>
            <Button onClick={onToLogin}>Уже зареган</Button>
            </div>
            <p className={styles.error}>{user_data.errorText}</p>
        </form>
    )
}

export default Signup;
