import React, {useState} from 'react';
import "./time-input.css"

export default class TimeInput extends React.Component {
    state = {
        hours: 0, minutes: 0,
    }
    render() {
        const [time_values, setTimeValues] = useState(state);
        return (
            <div class = "time-input">
                <input
                    id = "hours"
                    onInput = {this.goToNext}
                    className = "time-input__input" 
                    placeholder="00"
                    pattern="^(2[0-3]|[01]?[0-9])" required
                />
                <label class = "colon">:</label>    
                <input 
                    id = "minutes"
                    onInput = {this.goToPrev}
                    className = "time-input__input"
                    placeholder="00"
                    pattern="([0-5]?[0-9])$" required
                />
            </div>
        )
    }

    goToNext(event) {
        const hours = document.getElementById("hours");
        const minutes = document.getElementById("minutes");
        console.log(hours.textContent);
        console.log(minutes.textContent);
    }

    goToPrev(event) {
        console.log(event.target.value);
    }
}
