import React from "react";
import classnames from 'classnames'
import styles from "./index.module.css";

function Title({children, titleStyle, inversed}) {
    let title_style;
    switch(titleStyle) {
        case 0: 
            title_style = styles.main-title;
            break;
        case 1:
            title_style = styles.secondary-title;
            break;
        default:
            title_style = styles.secondary-title;    
    }
    const className = classnames(title_style, {
        [styles.inversed]: !!inversed
      });
    return (
        <h1 className={className}>{children}</h1>
    )
}


export default Title
