import React, {useState} from 'react';
import styles from './index.module.css';
import Star from './Star'


function RatingInput({rating, changeRating}) {
    const cnt = rating.rating
    const [colors, changeColors] = useState(["LIGHTGRAY", "LIGHTGRAY", "LIGHTGRAY", "LIGHTGRAY", "LIGHTGRAY"])
    for (let i = 0; i < cnt; i++) {
        colors[i] = "YELLOW";
    }

    changeColors(colors.map((idx) => {
        if (idx < cnt) {
            return "YELLOW";
        } else {
            return "GRAY"
        }
    }))

    const onFocus = () => {
        changeColors(["GRAY", "GRAY", "GRAY", "GRAY", "GRAY"])

    }
    return (
        <div className={styles.wrapper} onClick = {onFocus}>
            <Star color = {colors[0]}></Star>
            <Star color = {colors[1]}></Star>
            <Star color = {colors[2]}></Star>
            <Star color = {colors[3]}></Star>
            <Star color = {colors[4]}></Star>
        </div>
    )
}

export default RatingInput
